mapper.insert:
insert into reply_1to1(
reply_num, board_num, user_id, reply_title, text, new_date
)values(
reply_1to1_seq.nextVal, #{board_num}, #{user_id}, #{reply_title}, #{text}, sysdate
)

mapper.update:
update reply_1to1 set
reply_title=#{reply_title}, text=#{text}, new_date=sysdate
where reply_num=#{reply_num}
