mapper.insert:
	
		SELECT fairytales_seq.nextval FROM DUAL
	
INSERT INTO FAIRYTALES (
			 FAIRY_PK
			,FAIRY_NAME
			,FAIRY_CODE
			,FAIRY_CHAPTER
			,FAIRY_POSITION
			,FAIRY_LEVEL
		) VALUES (
			 #{fairy_pk}
			,#{fairy_name}
			,#{fairy_code}
			,#{fairy_chapter}
			,'/FairyTales/' || #{fairy_pk}
			,#{fairy_level}
		)

mapper.update:
UPDATE FAIRYTALES
   SET FAIRY_NAME		=	#{fairy_name}
	  ,FAIRY_CODE		=	#{fairy_code}
	  ,FAIRY_LEVEL		=	#{fairy_level}
 WHERE FAIRY_PK			=	#{fairy_pk}
